mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM user_challenge
    WHERE challenge_id = #{challengeId}
      AND user_id = #{userId}
)

SELECT EXISTS (
    SELECT 1
    FROM user_challenge
    WHERE user_id = #{userId}
      AND status = 'ongoing'
)

SELECT EXISTS (
    SELECT 1
    FROM user
    WHERE user_id = #{userId}
)

mapper.delete:
DELETE FROM user_challenge
WHERE challenge_id = #{challengeId}
  AND user_id = #{userId}

mapper.select:
SELECT c.challenge_id, c.category_id, c.title, c.summary, c.description FROM challenge c WHERE c.category_id IN
#{categoryId}
AND NOT EXISTS ( SELECT 1 FROM user_challenge uc WHERE uc.challenge_id = c.challenge_id AND uc.user_id = #{userId} AND uc.status = 'ongoing' ) ORDER BY c.challenge_id LIMIT 3
