mapper.update:
UPDATE expenditure
SET category_id      = #{categoryId},
    amount           = #{amount},
    description      = #{description},
    expenditure_date = #{expenditureDate}
WHERE expenditure_id = #{expenditureId}
  AND user_id = #{userId}

mapper.delete:
DELETE
FROM expenditure
WHERE expenditure_id = #{param1}
  AND user_id = #{param2}
